import React, { useState, useEffect, useContext } from "react";
import { AutenticadoContexto } from "../../Contexts/authContexts";
import { Link } from "react-router-dom";
import apiLocal from "../../services/api";
import { toast } from "react-toastify";
import fotoPerfil from '../../image/foto-perfil.png';

export default function UsuariosInfo() {
    const [dadosUsuarios, setDadosUsuarios] = useState(null);
    const { usuarioId } = useContext(AutenticadoContexto);
    const [novaFotoPerfil, setNovaFotoPerfil] = useState(null);
    const [previewFotoPerfil, setPreviewFotoPerfil] = useState(fotoPerfil);

    useEffect(() => {
        async function consultarDadosUsuarios() {
            try {
                const resposta = await apiLocal.get(`/ConsultarUsuariosUnico/${usuarioId}`);
                setDadosUsuarios(resposta.data);
                // Construa a URL inicial da imagem com segurança
                const fotoInicialUrl = resposta.data?.banner ? `/files/${resposta.data.banner}` : fotoPerfil;
                setPreviewFotoPerfil(fotoInicialUrl);
            } catch (err) {
                toast.error('Erro ao Comunicar com BackEnd', { toastId: 'ToastId' });
            }
        }

        consultarDadosUsuarios();
    }, [usuarioId]);

    const handleFotoPerfilChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setNovaFotoPerfil(file);
            setPreviewFotoPerfil(URL.createObjectURL(file));
        } else {
            setNovaFotoPerfil(null);
            const bannerUrl = dadosUsuarios?.banner ? `/files/${dadosUsuarios.banner}` : fotoPerfil;
            setPreviewFotoPerfil(bannerUrl);
        }
    };

    const handleEnviarFotoPerfil = async (event) => {
        event.preventDefault();

        if (!novaFotoPerfil) {
            toast.warn("Selecione uma foto de perfil para enviar.");
            return;
        }

        const formData = new FormData();
        formData.append('file', novaFotoPerfil);

        try {
            const resposta = await apiLocal.put(`/AlterarDadosUsuarios/${usuarioId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            toast.success("Foto de perfil atualizada com sucesso!");
            setDadosUsuarios(prevDadosUsuarios => ({
                ...prevDadosUsuarios,
                banner: resposta.data.banner
            }));
            setNovaFotoPerfil(null);
            // Construa a URL da nova foto com segurança
            const novaFotoUrl = resposta.data.banner ? `/files/${resposta.data.banner}` : fotoPerfil;
            setPreviewFotoPerfil(novaFotoUrl);
        } catch (error) {
            toast.error("Erro ao enviar a foto de perfil.");
            console.error("Erro ao enviar foto de perfil:", error);
        }
    };

    if (!dadosUsuarios) {
        return <p>Carregando...</p>;
    }

    // Use o encadeamento opcional para acessar create_at com segurança
    const dataFormatada = dadosUsuarios?.create_at ? new Date(dadosUsuarios.create_at).toLocaleDateString("pt-BR", {
        day: "numeric",
        month: "long",
        year: "numeric",
    }) : 'Data não disponível';

    return (
        <div>
            <div className="box-perfil-principal">
                <div className="box-perfil">
                    <div className="box-perfil-foto">
                        <img src={previewFotoPerfil} alt="Foto de perfil" style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }} />
                        <form onSubmit={handleEnviarFotoPerfil}>
                            <label htmlFor="upload-foto" className="botao-upload-foto">
                                Selecionar Foto
                            </label>
                            <input
                                id="upload-foto"
                                type="file"
                                accept="image/*"
                                onChange={handleFotoPerfilChange}
                                style={{ display: 'none' }}
                            />
                            {novaFotoPerfil && (
                                <button type="submit" className="botao-enviar-foto">Enviar Foto</button>
                            )}
                        </form>
                    </div>
                    <div className="box-perfil-nome">
                        <h2>{dadosUsuarios.nome}</h2>
                        <p>{dadosUsuarios.sobrenome}</p>
                    </div>
                </div>
                <ul className="box-perfil-lista">
                    <li><p>E-mail: {dadosUsuarios.email}</p></li>
                    <li><p>Idade: {dadosUsuarios.idade || "Não informada"}</p></li>
                    <li><p>Gênero: {dadosUsuarios.genero}</p></li>
                    <li><p>Membro desde: {dataFormatada}</p></li>
                </ul>

                <div className="box-perfil-button-voltar-editar">
                    <button><Link to={`/EditarUsuarios/${usuarioId}`}><p>Editar</p></Link></button>
                </div>
            </div>
        </div>
    );
}
