import React, { useState, useEffect, useContext } from "react";
import { AutenticadoContexto } from "../../Contexts/authContexts";
import { Link } from "react-router-dom";
import apiLocal from "../../services/api";
import { toast } from "react-toastify";
import fotoPerfil from '../../image/foto-perfil.png';

export default function UsuariosInfo() {
    const [dadosUsuarios, setDadosUsuarios] = useState(null);
    const { usuarioId } = useContext(AutenticadoContexto);
    const [fotoUrl, setFotoUrl] = useState(fotoPerfil); // Inicializa com a foto padrão
    const [novaFoto, setNovaFoto] = useState(null); // Estado para armazenar o arquivo selecionado
    const [loading, setLoading] = useState(false); // Estado para controlar o carregamento
    const [error, setError] = useState(null); // Estado para armazenar mensagens de erro

    useEffect(() => {
        async function consultarDadosUsuarios() {
            try {
                const resposta = await apiLocal.get(`/ConsultarUsuariosUnico/${usuarioId}`);
                setDadosUsuarios(resposta.data);
                // Inicializa a URL da foto com a URL do banner ou padrão
                setFotoUrl(resposta.data?.banner ? `/files/${resposta.data.banner}` : fotoPerfil);
            } catch (err) {
                console.error("Erro ao buscar dados do usuário:", err);
                toast.error('Erro ao Comunicar com BackEnd');
                setError("Erro ao buscar dados do usuário.");
            }
        }

        consultarDadosUsuarios();
    }, [usuarioId]);

    const handleFotoPerfilChange = (event) => {
        const file = event.target.files[0];
        if (file) {
            setNovaFoto(file); // Armazena o arquivo
            setFotoUrl(URL.createObjectURL(file)); // Pré-visualização
        } else {
            setNovaFoto(null);
            setFotoUrl(dadosUsuarios?.banner ? `/files/${dadosUsuarios.banner}` : fotoPerfil); 
        }
    };

    const handleEnviarFotoPerfil = async (event) => {
        event.preventDefault();
        setError(null); 
        if (!novaFoto) {
            toast.warn("Selecione uma foto de perfil para enviar.");
            setError("Selecione uma foto de perfil para enviar.");
            return;
        }

        const formData = new FormData();
        formData.append('file', novaFoto);

        setLoading(true); 
        try {
            const resposta = await apiLocal.put(`/AlterarDadosUsuarios/${usuarioId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                },
            });
            console.log('Resposta do backend:', resposta.data);
            toast.success("Foto de perfil atualizada com sucesso!");
            setDadosUsuarios(prevDadosUsuarios => ({
                ...prevDadosUsuarios,
                banner: resposta.data.banner
            }));
            
            setFotoUrl(resposta.data.banner ? `/files/${resposta.data.banner}` : fotoPerfil);
            setNovaFoto(null); 
        } catch (error) {
            console.error("Erro ao enviar foto:", error);
            toast.error("Erro ao enviar a foto de perfil.");
            setError("Erro ao enviar a foto de perfil.");
        } finally {
            setLoading(false); 
        }
    };

    if (!dadosUsuarios) {
        return <p>Carregando...</p>;
    }

    const dataFormatada = dadosUsuarios?.create_at ? new Date(dadosUsuarios.create_at).toLocaleDateString("pt-BR", {
        day: "numeric",
        month: "long",
        year: "numeric",
    }) : 'Data não disponível';

    return (
        <div>
            <div className="box-perfil-principal">
                <div className="box-perfil">
                    <div className="box-perfil-foto">
                        <img src={fotoUrl} alt="Foto de perfil" style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }} />
                        <form onSubmit={handleEnviarFotoPerfil}>
                            <label htmlFor="upload-foto" className="botao-upload-foto">
                                Selecionar Foto
                            </label>
                            <input
                                id="upload-foto"
                                type="file"
                                accept="image/*"
                                onChange={handleFotoPerfilChange}
                                style={{ display: 'none' }}
                            />
                            {novaFoto && (
                                <button type="submit" className="botao-enviar-foto" disabled={loading}>
                                    {loading ? "Enviando..." : "Enviar Foto"}
                                </button>
                            )}
                        </form>
                        {error && <p className="error-message">{error}</p>}
                    </div>
                    <div className="box-perfil-nome">
                        <h2>{dadosUsuarios.nome}</h2>
                        <p>{dadosUsuarios.sobrenome}</p>
                    </div>
                </div>
                <ul className="box-perfil-lista">
                    <li><p>E-mail: {dadosUsuarios.email}</p></li>
                    <li><p>Idade: {dadosUsuarios.idade || "Não informada"}</p></li>
                    <li><p>Gênero: {dadosUsuarios.genero}</p></li>
                    <li><p>Membro desde: {dataFormatada}</p></li>
                </ul>

                <div className="box-perfil-button-voltar-editar">
                    <button><Link to={`/EditarUsuarios/${usuarioId}`}><p>Editar</p></Link></button>
                </div>
            </div>
        </div>
    );
}
