import React, { useState, useEffect, useContext, useRef } from "react";
import { AutenticadoContexto } from "../../Contexts/authContexts"; // Ajuste o caminho se necessário
import { useNavigate } from "react-router-dom"; // Importe useNavigate
import apiLocal from "../../services/api"; // Ajuste o caminho se necessário
import { toast } from "react-toastify";
import fotoPerfil from '../../image/foto-perfil.png'; // Ajuste o caminho se necessário

export default function UsuariosInfo() {
    const [dadosUsuarios, setDadosUsuarios] = useState(null);
    const [fotoUrl, setFotoUrl] = useState(fotoPerfil); // URL da imagem a ser exibida
    const [novaFoto, setNovaFoto] = useState(null); // Arquivo da nova foto selecionada (File object)
    const [loading, setLoading] = useState(false); // Para indicar carregamento inicial e de upload
    const [uploading, setUploading] = useState(false); // Específico para o processo de upload
    const [error, setError] = useState(null); // Para mensagens de erro

    const { usuarioId } = useContext(AutenticadoContexto);
    const navigate = useNavigate(); // Hook para navegação programática

    // Ref para guardar a URL do objeto de preview e revogá-la depois
    const objectUrlRef = useRef(null);

    // Efeito para buscar dados do usuário ao montar o componente ou quando usuarioId mudar
    useEffect(() => {
        async function consultarDadosUsuarios() {
            if (!usuarioId) return; // Não fazer nada se não houver ID

            setLoading(true); // Inicia carregamento inicial
            setError(null);
            try {
                const resposta = await apiLocal.get(`/ConsultarUsuariosUnico/${usuarioId}`);
                setDadosUsuarios(resposta.data);
                const bannerInicial = resposta.data?.banner;
                setFotoUrl(bannerInicial ? `http://localhost:3333/files/${bannerInicial}` : fotoPerfil);

                // Define a URL da foto inicial (do banco ou padrão)
                setFotoUrl(resposta.data?.banner ? `/files/${resposta.data.banner}` : fotoPerfil);

            } catch (err) {
                console.error("Erro ao buscar dados do usuário:", err);
                toast.error('Erro ao buscar dados do usuário.');
                setError("Não foi possível carregar os dados do usuário.");
            } finally {
                setLoading(false); // Termina carregamento inicial
            }
        }

        consultarDadosUsuarios();

        // Função de limpeza para revogar URL do objeto ao desmontar
        return () => {
            if (objectUrlRef.current) {
                URL.revokeObjectURL(objectUrlRef.current);
                objectUrlRef.current = null;
            }
        };
    }, [usuarioId]); // Dependência: busca novamente se o usuarioId mudar

    // Função chamada quando o input de arquivo muda
    const handleFotoPerfilChange = (event) => {
        const file = event.target.files[0];

        // Revoga a URL do objeto anterior, se existir
        if (objectUrlRef.current) {
            URL.revokeObjectURL(objectUrlRef.current);
            objectUrlRef.current = null;
        }

        if (file && file.type.startsWith('image/')) {
            setNovaFoto(file); // Guarda o arquivo selecionado

            // Cria uma URL de objeto para preview e guarda a referência
            objectUrlRef.current = URL.createObjectURL(file);
            setFotoUrl(objectUrlRef.current); // Atualiza a imagem exibida para o preview
        } else {
            // Se nenhum arquivo for selecionado ou o tipo for inválido, reseta
            setNovaFoto(null);
            // Volta para a imagem atual do usuário ou a padrão
            setFotoUrl(dadosUsuarios?.banner ? `/files/${dadosUsuarios.banner}` : fotoPerfil);
            if (file) { // Se um arquivo foi selecionado mas era inválido
                toast.warn("Por favor, selecione um arquivo de imagem válido (jpeg, png, etc).");
            }
        }
    };

    // Função chamada ao enviar o formulário de upload
    const handleEnviarFotoPerfil = async (event) => {
        event.preventDefault(); // Impede o envio padrão do formulário
        setError(null);

        if (!novaFoto) {
            toast.warn("Nenhuma nova foto selecionada para enviar.");
            setError("Selecione uma foto de perfil para enviar.");
            return;
        }

        const formData = new FormData();
        formData.append('file', novaFoto); // Adiciona o arquivo ao FormData com a chave 'file'

        setUploading(true); // Inicia indicador de upload

        try {
            // Faz a requisição PUT para o backend
            const resposta = await apiLocal.put(`/AlterarDadosUsuarios/${usuarioId}`, formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    // Authorization header é adicionado automaticamente pelo interceptor do axios, se configurado.
                    // Se não, adicione manualmente: Authorization: `Bearer ${token}` (obtenha o token do contexto)
                },
            });

            // --- SUCESSO NO UPLOAD ---
            console.log('API Response (Upload Success):', resposta.data);

            const novoNomeBanner = resposta.data.banner; // Pega o nome do arquivo da resposta

            if (!novoNomeBanner) {
                throw new Error("Resposta da API não incluiu o nome do banner atualizado.");
            }

            const novaUrlImagem = `/files/${novoNomeBanner}`;
            console.log('New image URL to set:', novaUrlImagem);

            // Atualiza o estado com os novos dados
            setDadosUsuarios(prevDados => ({
                ...prevDados,
                banner: novoNomeBanner // Atualiza o nome do banner nos dados locais do usuário
            }));
            setFotoUrl(novaUrlImagem); // ATUALIZA A URL DA IMAGEM EXIBIDA

            setNovaFoto(null); // Limpa a seleção de nova foto
            toast.success("Foto de perfil atualizada com sucesso!");

            // Limpa a URL do objeto de preview que não é mais necessária
            if (objectUrlRef.current) {
                URL.revokeObjectURL(objectUrlRef.current);
                objectUrlRef.current = null;
            }

        } catch (error) {
            console.error("Erro ao enviar foto:", error);
            toast.error("Erro ao enviar a foto de perfil. Verifique o console para detalhes.");
            setError("Erro ao enviar a foto de perfil.");
            // Opcional: reverter a URL da imagem se o upload falhar?
            // setFotoUrl(dadosUsuarios?.banner ? `/files/${dadosUsuarios.banner}` : fotoPerfil);
        } finally {
            setUploading(false); // Termina indicador de upload
            // Limpa o valor do input de arquivo para permitir selecionar o mesmo arquivo novamente, se necessário
            event.target.reset(); // Reseta o formulário (que contém o input file)
        }
    };

    // --- RENDERIZAÇÃO ---

    // Exibe estado de carregamento inicial
    if (loading) {
        return <p>Carregando dados do perfil...</p>;
    }

    // Exibe erro de carregamento inicial
    if (error && !dadosUsuarios) {
        return <p style={{ color: 'red' }}>{error}</p>;
    }

    // Se não houver dados do usuário (mesmo sem erro), exibe mensagem
    if (!dadosUsuarios) {
        return <p>Não foi possível carregar as informações do perfil.</p>;
    }

    // Formata a data de criação (apenas se existir)
    const dataFormatada = dadosUsuarios.create_at ? new Date(dadosUsuarios.create_at).toLocaleDateString("pt-BR", {
        day: "numeric",
        month: "long",
        year: "numeric",
    }) : 'Data não disponível';

    return (
        <div>
            {/* Use suas classes CSS como no exemplo original */}
            <div className="box-perfil-principal">
                <div className="box-perfil">
                    <div className="box-perfil-foto">
                        {/* Imagem do Perfil */}
                        <img
                            src={fotoUrl} // Controlado pelo estado fotoUrl
                            alt="Foto de perfil"
                            style={{ width: '100px', height: '100px', borderRadius: '50%', objectFit: 'cover' }}
                            onError={(e) => {
                                // Se a imagem der erro ao carregar (ex: arquivo deletado), volta para a padrão
                                console.warn(`Erro ao carregar imagem: ${fotoUrl}. Voltando para padrão.`);
                                e.target.src = fotoPerfil;
                            }}
                        />

                        {/* Formulário de Upload */}
                        <form onSubmit={handleEnviarFotoPerfil}>
                            <label htmlFor="upload-foto" className="botao-upload-foto">
                                {uploading ? "Enviando..." : "Selecionar Foto"}
                            </label>
                            <input
                                id="upload-foto"
                                type="file"
                                accept="image/*" // Aceita qualquer tipo de imagem
                                onChange={handleFotoPerfilChange}
                                style={{ display: 'none' }} // Input fica escondido
                                disabled={uploading} // Desabilita enquanto envia
                            />
                            {/* Botão de Enviar aparece apenas se uma nova foto foi selecionada */}
                            {novaFoto && (
                                <button type="submit" className="botao-enviar-foto" disabled={uploading}>
                                    {uploading ? "Enviando..." : "Enviar Nova Foto"}
                                </button>
                            )}
                        </form>
                        {/* Exibe erro de upload */}
                        {error && <p style={{ color: 'red', marginTop: '10px' }}>{error}</p>}
                    </div>

                    {/* Informações do Usuário */}
                    <div className="box-perfil-nome">
                        <h2>{dadosUsuarios.nome}</h2>
                        <p>{dadosUsuarios.sobrenome}</p>
                    </div>
                </div>

                <ul className="box-perfil-lista">
                    <li><p>E-mail: {dadosUsuarios.email}</p></li>
                    <li><p>Idade: {dadosUsuarios.idade ?? "Não informada"}</p></li>
                    <li><p>Gênero: {dadosUsuarios.genero}</p></li>
                    <li><p>Membro desde: {dataFormatada}</p></li>
                </ul>
                <div className="box-perfil-button-voltar-editar">
                    <button onClick={() => navigate(`/EditarUsuarios/${usuarioId}`)} disabled={uploading}>
                        Editar Perfil
                    </button>

                </div>
            </div>
        </div>
    );
}
