import React, { useState, useEffect, useContext } from "react";
import { AutenticadoContexto } from "../../Contexts/authContexts";
import api from "../../services/api";
import search from "../../image/logo-search.png";
import cordero_malbec from "../../image/cordero-malbec.png";
import barril from "../../image/barril.png";
import uvas from "../../image/uvas.png";
import pais_regiao from "../../image/pais-regiao.png";
import star from "../../image/star.png";
import infoadd_white from "../../image/infoadd_white.png";
import { Link } from "react-router-dom";
import ModalFiltro from "../../components/ModalFiltro";

export default function Lista() {
    const [query, setQuery] = useState("");
    const [listas, setListas] = useState([]);
    const [error, setError] = useState("");
    const [modalAberto, setModalAberto] = useState(false);
    const [filtroAtivo, setFiltroAtivo] = useState(null);
    const [opcoesFiltro, setOpcoesFiltro] = useState([]);
    const [filtroSelecionado, setFiltroSelecionado] = useState(null);
    const { usuarioId } = useContext(AutenticadoContexto);

    useEffect(() => {
        async function consultarListaInterna(filtro = {}) {
            try {
                const response = await api.post(`/FiltrarLista/${usuarioId}`, filtro);
                setListas(response.data);
                setError("");
            } catch (error) {
                setError("Nenhum vinho encontrado com este filtro.");
                setListas([]);
            }
        }

        if (usuarioId) {
            consultarListaInterna(); // Busca a lista inicial
        }

        // Use a função interna diretamente nas outras funções
        const handleCloseModalInterno = () => {
            setModalAberto(false);
            setFiltroAtivo(null);
            setOpcoesFiltro([]);
            setFiltroSelecionado(null);
            consultarListaInterna();
        };

        const handleSelectFiltroInterno = (opcao) => {
            setFiltroSelecionado(opcao);
            setModalAberto(false);
            const filtro = {};
            if (filtroAtivo === 'uva') {
                filtro.uva = opcao;
            } else if (filtroAtivo === 'pais') {
                filtro.pais = opcao;
            } else if (filtroAtivo === 'vinicola') {
                filtro.vinicola = opcao;
            }
            consultarListaInterna(filtro);
        };

        setHandleCloseModal(() => handleCloseModalInterno);
        setHandleSelectFiltro(() => handleSelectFiltroInterno);

    }, [usuarioId, api]);

    const [handleCloseModal, setHandleCloseModal] = useState(() => {});
    const [handleSelectFiltro, setHandleSelectFiltro] = useState(() => {});

    const fetchOpcoesFiltro = async (tipoFiltro) => {
        try {
            const response = await api.get(`/OpcoesFiltro/${tipoFiltro}`);
            setOpcoesFiltro(response.data);
        } catch (error) {
            console.error(`Erro ao buscar opções de ${tipoFiltro}:`, error);
            setOpcoesFiltro([]);
        }
    };

    const handleOpenModal = (tipoFiltro) => {
        setFiltroAtivo(tipoFiltro);
        fetchOpcoesFiltro(tipoFiltro);
        setModalAberto(true);
    };

    const handleSearch = (e) => {
        setQuery(e.target.value);
    };

    const filteredVinhos = listas.flatMap(lista =>
        lista.vinhos.map(listaVinho => listaVinho.vinho).filter(vinho =>
            vinho.nome.toLowerCase().includes(query.toLowerCase()) ||
            vinho.uva.toLowerCase().includes(query.toLowerCase())
        )
    );
    return (
        <div className="box-lista">
            <div className="box-lista-search">
                <form onSubmit={(e) => e.preventDefault()}>
                    <div className="box-search-pesquisa">
                        <input
                            type="search"
                            placeholder="Procurar..."
                            value={query}
                            onChange={handleSearch}
                        />
                        <button type="submit">
                            <img src={search} alt="Pesquisar" />
                        </button>
                    </div>
                </form>
            </div>

            <div className="box-filter">
                <button onClick={() => handleOpenModal('uva')}>
                    <img src={uvas} alt="" />
                    <p>Uvas</p>
                </button>
                <button onClick={() => handleOpenModal('pais')}>
                    <img src={pais_regiao} alt="" />
                    <p>País/Região</p>
                </button>
                <button onClick={() => handleOpenModal('vinicola')}>
                    <img src={barril} alt="" />
                    <p>Vinicola</p>
                </button>
            </div>

            <ModalFiltro
                isOpen={modalAberto}
                onClose={handleCloseModal}
                options={opcoesFiltro}
                onSelect={handleSelectFiltro}
                title={filtroAtivo === 'uva' ? 'Selecione a Uva' : filtroAtivo === 'pais' ? 'Selecione o País/Região' : filtroAtivo === 'vinicola' ? 'Selecione a Vinícola' : ''}
            />

            <div className="box-lista-resposta">
                {error && <p className="error-message">{error}</p>}

                <ul className="box-resposta-ul">
                    {filteredVinhos.map((vinho) => (
                        <li key={vinho.id}>
                            <img src={cordero_malbec} alt={vinho.nome} />
                            <div className="box-resposta-nome">
                                <p>{vinho.nome} 750ml</p>
                                <p>{vinho.tipo}</p>
                                <p>{vinho.uva}</p>
                                <div className="box-resposta-info">
                                    <Link to={`/vinhoInformacoes/${vinho.id}`}>
                                        <img src={infoadd_white} alt="informações adicionais" />
                                        <p>Mais informações</p>
                                    </Link>
                                    <div className="box-resposta-nota">
                                        <div className="box-nota-item">
                                            <img src={star} alt="" />
                                            <p>{vinho.nota_media}</p>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </li>
                    ))}
                </ul>
                {filteredVinhos.length === 0 && !error && query && <p>Nenhum vinho encontrado com o termo "{query}".</p>}
                {listas.length === 0 && !error && !query && <p>Sua lista de vinhos está vazia.</p>}
            </div>
        </div>
    );
}
