import React, { useState, useEffect, useContext } from "react";
import { AutenticadoContexto } from "../../Contexts/authContexts";
import api from "../../services/api";
import search from "../../image/logo-search.png";
import cordero_malbec from "../../image/cordero-malbec.png";
import barril from "../../image/barril.png";
import uvas from "../../image/uvas.png";
import pais_regiao from "../../image/pais-regiao.png";
import star from "../../image/star.png";
import infoadd_white from "../../image/infoadd_white.png";
import { Link } from "react-router-dom";

export default function Lista() {
    const [query, setQuery] = useState("");
    const [listas, setListas] = useState([]);
    const [error, setError] = useState("");
    const [modalAbertoUva, setModalAbertoUva] = useState(false);
    const [modalAbertoPais, setModalAbertoPais] = useState(false);
    const [modalAbertoVinicola, setModalAbertoVinicola] = useState(false);
    const [modalEditarAberto, setModalEditarAberto] = useState(false);
    const [vinhoParaEditar, setVinhoParaEditar] = useState(null);
    const { usuarioId } = useContext(AutenticadoContexto);

    useEffect(() => {
        async function consultarLista() {
            try {
                const response = await api.get(`/ConsultarLista/${usuarioId}`);
                setListas(response.data);
            } catch (error) {
                setError("Nenhum vinho encontrado.");
            }
        }
        if (usuarioId) {
            consultarLista();
        }
    }, [query, usuarioId]);

    return (
        <div className="box-lista">
            <div className="box-lista-search">
                <form>
                    <div className="box-search-pesquisa">
                        <input
                            type="search"
                            placeholder="Procurar..."
                            value={query}
                            onChange={(e) => setQuery(e.target.value)}
                        />
                        <button type="submit">
                            <img src={search} alt="Pesquisar" />
                        </button>
                    </div>
                </form>
            </div>

            <div className="box-filter">
                <button onClick={() => setModalAbertoUva(true)}>
                    <img src={uvas} alt="" />
                    <p>Uvas</p>
                </button>
                <button onClick={() => setModalAbertoPais(true)}>
                    <img src={pais_regiao} alt="" />
                    <p>País/Região</p>
                </button>
                <button onClick={() => setModalAbertoVinicola(true)}>
                    <img src={barril} alt="" />
                    <p>Vinicola</p>
                </button>
            </div>

            <div className="box-lista-resposta">
                {error && <p>{error}</p>}

                <ul className="box-resposta-ul">
                    {filteredVinhos.map((vinho) => (
                        <li key={vinho.id}>
                            <div className="box-resposta-editar">
                                <img src={cordero_malbec} alt="" />
                                <button onClick={() => abrirModalEditar(vinho)}>Editar</button>
                            </div>
                            <div className="box-resposta-nome">
                                <p>{vinho.nome} 750ml</p>
                                <p>{vinho.uva}</p>
                                <div className="box-resposta-info">
                                    <Link to={`/vinhoInformacoes/${vinho.id}`}>
                                        <img src={infoadd_white} alt="informações adicionais" />
                                        Mais informações
                                    </Link>
                                    <div className="box-resposta-nota">
                                        <img src={star} alt="" />
                                        <p>{vinho.nota_media}</p>
                                    </div>
                                </div>
                            </div>
                            {modalAbertoUva && (
                                <div className="box-modal-uva">
                                    <div className="modal-uva">
                                        {vinho.uva}
                                    </div>
                                </div>
                            )}
                            {modalAbertoPais && (
                                <div className="box-modal-pais">
                                    <div className="modal-pais">
                                    </div>
                                </div>
                            )}
                            {modalAbertoVinicola && (
                                <div className="box-modal-Vinicola">
                                    <div className="modal-vinicola">
                                       
                                    </div>
                                </div>
                            )}
                        </li>
                    ))}
                </ul>
                {modalAbertoUva && (
                    <div className="box-modal-uva">
                        <div className="modal-uva">
                        
                        </div>
                    </div>
                )}
                {modalAbertoPais && (
                    <div className="box-modal-pais">
                        <div className="modal-pais">
                           
                        </div>
                    </div>
                )}
                {modalAbertoVinicola && (
                    <div className="box-modal-Vinicola">
                        <div className="modal-vinicola">
                           
                        </div>
                    </div>
                )}

                {modalEditarAberto && vinhoParaEditar && (
                    <div className="modal-overlay">
                        <div className="modal-editar">
                            <h3>Editar/Apagar Vinho</h3>
                           
                            <label>Nome:</label>
                            <input type="text" defaultValue={vinhoParaEditar.nome} />
                            <label>Uva:</label>
                            <input type="text" defaultValue={vinhoParaEditar.uva} />
                          
                            <div className="modal-buttons">
                                <button onClick={fecharModalEditar}>Cancelar</button>
                                <button onClick={() => handleEditarVinho({ id: vinhoParaEditar.id, nome: 'Novo Nome', uva: 'Nova Uva' })}>
                                    Salvar Edições
                                </button>
                                <button className="button-apagar" onClick={() => handleApagarVinho(vinhoParaEditar.id)}>
                                    Apagar Vinho
                                </button>
                            </div>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}
